<?php

/**
 * PaymentTypeTable
 * 
 * This class has been auto-generated by the Doctrine ORM Framework
 */
class PaymentTypeTable extends Doctrine_Table
{
    /**
     * Returns an instance of this class.
     *
     * @return object PaymentTypeTable
     */
    public static function getInstance()
    {
        return Doctrine_Core::getTable('PaymentType');
    }

    public function getCurrentCompany()
    {
        $query = $this->createQuery('q')
                ->where('q.company_id = ?', sfContext::getInstance()->getUser()->getAttribute('company_id'))
                ->orderBy('name ASC');
        $result = $query->execute();
        return $result;
    }
}
